import React, { Component } from 'react';
import PopSearchBox from '../../components/search-box/PopSearchBox';
import './PopSearchHeaderStyles.scss'

import { connect } from 'react-redux';


const mapStateToProps = (state) => {
	return {
		p_search: state.pkeyword.p_keyword
	}
};

class PopSearchHeader extends Component {
	constructor() {
		super()
		this.sate = {
			p_searchkey: ""
		}
	}

	componentDidMount() {
		this.setState({p_searchkey: this.props.p_search})
	}


	render() {
		// const{ p_search } = this.props;
		// console.log(this.props.p_search)

		return(
			<div>
				<h1 className='pagetitle'>Pop Music Search</h1>
				<PopSearchBox />
				{console.log(this.state.p_searchkey)}
			</div>
		);
	}
}




export default connect(mapStateToProps)(PopSearchHeader);



