import React, { Component } from 'react';
import PopSearchBox from '../../components/search-box/PopSearchBox';
import './PopSearchHeaderStyles.scss'

import { connect } from 'react-redux';
import { change_pop_keyword } from '../../redux/pkeyword-action';


const mapStateToProps = state => {
	return {
		p_search: state.pkeyword.p_keyword
	}
};

const mapDispatchToProps = dispatch => {
	return {
		change_pkeyword: (event) => dispatch(change_pop_keyword(event))
	}
}

class PopSearchHeader extends Component {
	constructor() {
		super();
		this.state = {p_searchkey: ""};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
    this.setState({p_searchkey: event.target.value});
    console.log(this.state.p_searchkey)
    change_pop_keyword(this.state.p_searchkey);
  }

	// componentDidMount() {
	// 	this.setState({p_searchkey: this.props.p_search})
	// }


	render() {
		const { p_search, change_pkeyword } = this.props;

		return(
			<div>
				<h1 className='pagetitle'>Pop Music Search</h1>
				<PopSearchBox pKeywordChange={this.handleChange}/>
				<h4>{ p_search }</h4>
			</div>
		);
	}
}


export default connect(mapStateToProps, mapDispatchToProps)(PopSearchHeader);



