import React, { Component, useEffect, useState } from 'react';
import { connect } from 'react-redux';

import PopCard from './PopCard';
import './PopCardListStyles.scss';

const mapStateToProps = (state) => {
	return {
		p_keyword: state.pkeyword.p_keyword
	}
}

class PopCardList extends Component {
	constructor() {
		super();
		this.state = {
			popinfo: [],
			pkw: ""
		};
	}

	// componentDidMount() {
	// 	{console.log(this.props.p_keyword)}
	// 	this.setState({ pkw: this.props.p_keyword });
	// 	var p_kw = this.state.pkw.substring(1, this.state.pkw.length-1);

	// 	fetch(`/contemporary-search?q=${p_kw}`)
 //    	.then(response => response.json())
 //    	.then(data => {this.setState({ popinfo: data })});
 //    {console.log("this is component did mount")}
	// }

	render() {
		const { popinfo } = this.state.popinfo;
		var pkw = (JSON.stringify(this.props.p_keyword));
		pkw = pkw.substring(1, pkw.length-1);

		fetch(`/contemporary-search?q=${pkw}`)
			.then(response => response.json())
			.then(data => {this.setState({ popinfo: data })});


		return(
			<div className='info-preview'>
				{
					popinfo && popinfo.map((artist, i) => {
						return (
							<PopCard 
							key={i} 
							id={popinfo[i].id} 
							name={popinfo[i].artist} 
							song={popinfo[i].name}
							album={popinfo[i].album}
							date={popinfo[i].date} 
							yt_url={popinfo[i].youtube}
							/>
						);
					})
				}
				{console.log(this.props.p_keyword)}
				{console.log("this is pop card render")}
		   </div> 
		);
	}
}



export default connect(mapStateToProps)(PopCardList);






