import React from 'react';
import './PopCardStyles.scss';
import {withRouter} from 'react-router-dom';

import { compose } from 'redux';
import { connect } from 'react-redux';
import { change_cardinfo } from '../../redux/cardInfo-action';

const mapDispatchToProps = dispatch => {
	return {
		change_cardinfo: (info) => dispatch(change_cardinfo(info))
	}
}


const PopCard = ({id, artist, name, album, date, yt_url, history, match, change_cardinfo}) => {
// const PopCard = ({popinfo, history, match, change_cardinfo}) => {
	// const { artist, name, album, date, yt_url } = popinfo;
	const linkUrl = 'pop_search_yt';
  const handleClick = (artist, name, album, date, yt_url) => {
  	const thisCard = {
		  artist: artist,
		  name: name,
		  album: album,
		  date: date,
		  youtube: yt_url,
		};
    change_cardinfo(thisCard);
    // history.push(`${linkUrl}`);
    history.push("/pop_search_yt");
  }

	return(
		<div className='dib br4 pa3 ma2 grow bw2 shadow-5 card-display'>
			<h2 className='song-font'>{name}</h2>
			<h3 className='singer-font'>by {artist}</h3>
			<h4 className='album-font'>Album/Single: {album}</h4>
			<p className='realease-date'>Realease date: {date}</p>
			<button 
				className="p-btn p-btn-one p-box-container"
				onClick={() =>
					handleClick(artist, name, album, date, yt_url)}
			>
				HIT THIS SONG
			</button>
		</div>
	)
}


export default compose(
  withRouter,
  connect(null, mapDispatchToProps)
)(PopCard);





