import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';

import PopCard from './PopCard';
import './PopCardListStyles.scss';

const mapStateToProps = (state) => {
	return {
		p_keyword: state.pkeyword.p_keyword
	}
}

function PopCardList (props) {
	const [popinfo, setPopinfo] = useState([]);
	const pkw = JSON.stringify(props.p_keyword);

  useEffect(() => {
    fetch("/contemporary-search?q=" + pkw)
    	.then(response => response.json())
    	.then(data => {setPopinfo(data.popinfo);});
  }, []);


	return(
		<div className='info-preview'>
			{
				popinfo.map((artist, i) => {
					return (
						<PopCard 
						key={i} 
						id={popinfo[i].id} 
						name={popinfo[i].name} 
						song={popinfo[i].song}
						album={popinfo[i].album}
						date={popinfo[i].date} 
						yt_url={popinfo[i].url}
						/>
					);
				})
			}
	   </div> 
	);
}



export default connect(mapStateToProps)(PopCardList);









