import React, { useState }  from 'react';
import { connect } from 'react-redux';

const mapStateToProps = (state) => ({
  songInfo: state.pcardInfo.pcard_info
});



function RelatedInfo(ckeckedInfo, songInfo) {
	if (ckeckedInfo.same_album) {
		// console.log(songInfo.album)
		// console.log(`/samealbum/${songInfo.album}`)
		fetch(`/samealbum/${songInfo.album}`)
    	.then(response => response.json())
    	.then(data => 
    		{
    			const albumRelated = JSON.parse(JSON.stringify(data));
    			console.log(albumRelated);
    		}
    	);
	} 
    else {
        const albumRelated = {};
    };

	if (ckeckedInfo.this_artiste) {
		// console.log(songInfo.artist)
		// console.log(`/sameartist/${songInfo.artist}`)
		fetch(`/sameartist/${songInfo.artist}`)
    	.then(response => response.json())
    	.then(data => 
    		{
    			const artisteRelated = JSON.parse(JSON.stringify(data));
    			console.log(artisteRelated);
    		}
    	);
	} else {
        const artisteRelated = {};
    }

	if (ckeckedInfo.genre) {
		// console.log(songInfo.genre)
		// console.log(`/samegenre/${songInfo.genre}`)
		fetch(`/samegenre/${songInfo.genre}`)
    	.then(response => response.json())
    	.then(data => 
    		{
    			const genreRelated = JSON.parse(JSON.stringify(data));
    			console.log(genreRelated);
    		}
    	);
	} else {
        const genreRelated = {};
    }

	if (ckeckedInfo.era) {
		// console.log(songInfo.date)
		// console.log(`/sameera/${songInfo.date}`)
		fetch(`/sameera/${songInfo.date}`)
    	.then(response => response.json())
    	.then(data => 
    		{
    			const eraRelated = JSON.parse(JSON.stringify(data));
    			console.log(eraRelated);
    		}
    	);
	} else {
        const eraRelated = {};
    }

	if (ckeckedInfo.associate_artisie) {
		fetch(`/associates/${songInfo.artist}/${songInfo.genre}`)
    	.then(response => response.json())
    	.then(data => 
    		{
    			const associateRelated = JSON.parse(JSON.stringify(data));
    			console.log(associateRelated);
    		}
    	);
	} else {
        const associateRelated = {};
    }

    // const combine = albumRelated;
}

// export default connect(mapStateToProps)(pRelatedInfo);
export default RelatedInfo;
