import React from 'react';

const Card = ({ id, name, email }) => {
  return(
  	//bg-light-green 為背景顏色
  	//dib 為排在同一行
  	//br 3, border 3 方匡四邊變弧形
  	//pa 3, padding 3
  	//ma2, margin 2 邊界（白邊界方開）
  	//grow 滑鼠移到時，會變大 
  	<div className='bg-light-green dib br3 pa1 ma2 grow bw2 shadow-5'>
  		<img alt='robots' src={`https://robohash.org/${id}?100x100`} />
  		<div>
    		<h2>{name}</h2>
    		<p>{email}</p>
  		</div>
	  </div>
	);
}

export default Card;
